import React, { useState, useEffect } from "react";
import Web3 from 'web3';
import GiftNFT from "./contracts/GiftNFT.json";

function App() {
  const [message, setMessage] = useState("");
  const [recipient, setRecipient] = useState("");
  const [tokenId, setTokenId] = useState("");

  useEffect(() => {
    const init = async () => {
      const web3 = new Web3(window.ethereum);
      const accounts = await web3.eth.requestAccounts();
      const defaultAccount = accounts[0];

      const contractAddress = "0xaed8166414dc429365e56e3b873c1d651879d154"; // コントラクトアドレスを入力
      const contract = new web3.eth.Contract(GiftNFT.abi, contractAddress, { from: defaultAccount });

      const giftNFT = async () => {
        try {
          const tx = await contract.methods.giftNFT(recipient, message).send({ from: defaultAccount });
          const event = tx.events[0];
          const newTokenId = event.args[2].toNumber();
          setTokenId(newTokenId);
        } catch (error) {
          console.error(error);
        }
      };
    }
    init();
  }, [message, recipient]); // 依存するステートの配列

  return (
    <div>
      <h1>Gift NFT</h1>
      <label>
        Message:
        <input type="text" onChange={(e) => setMessage(e.target.value)} />
      </label>
      <br />
      <label>
        Recipient:
        <input type="text" onChange={(e) => setRecipient(e.target.value)} />
      </label>
      <br />
      <button onClick={giftNFT}>Gift</button>
      {tokenId && (
        <div>
          <p>Gifted NFT with Token ID: {tokenId}</p>
          <p>Message: {message}</p>
          <p>Recipient: {recipient}</p>
        </div>
      )}
    </div>
  );
}
